import React from 'react';
import '../styles/home.css';

import { Container, Row, Col } from 'reactstrap';
import heroImg01 from '../assets/images/hero-img01.jpg';
import heroImg02 from '../assets/images/hero-img02.jpg';
import heroImg03 from '../assets/images/hero-img03.jpg';
import Subtitle from './../shared/Subtitle';

const Home = () => {
    return <>
    
    {/* hero section start */}
        <section>
            <Container>
                <Row>
                    <Col lg='6'>
                        <div className="hero_content">
                            <div className="hero_subtitle align-items-center">
                                <h1>Explore Hong Kong</h1>
                                <p>Welcome to the vibrant city of Hong Kong, where East truly meets West! From the iconic skyline of Victoria Harbour to the bustling streets of Mong Kok, Hong Kong offers a kaleidoscope of experiences for every traveler. Discover the breathtaking beauty of Lantau Island or take a tram ride to Victoria Peak for panoramic views of the city. With its rich cultural heritage, delectable cuisine, and awe-inspiring attractions, Hong Kong is a destination that will captivate your senses and leave you with memories to cherish forever. Come and experience the enchantment of Hong Kong â€“ an adventure like no other awaits!</p>
                            </div>
                        </div>
                    </Col>

                    <Col lg='2' className='align-items-center'>
                        <div className='hero_img-box'>
                            <img src={heroImg01} alt="" />
                        </div>
                    </Col>
                    <Col lg='2'>
                        <div className='hero_img-box'>
                            <img src={heroImg02} alt="" />
                        </div>
                    </Col>
                    <Col lg='2'>
                        <div className='hero_img-box'>
                            <img src={heroImg03} alt="" />
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>

    </>;
};

export default Home;
